mapper.select:
SELECT
FROM sys_menu a WHERE a.id IN
#{item}

mapper.delete:
        UPDATE sys_menu SET
        del_flag = 1,
        modifier = #{modifier} ,
        modify_date = #{modifyDate, jdbcType=TIMESTAMP, javaType=java.util.Date}
		WHERE id = #{id}

        UPDATE sys_menu SET
        del_flag = 1,
        modifier = #{modifier} ,
        modify_date = #{modifyDate, jdbcType=TIMESTAMP, javaType=java.util.Date}
		WHERE tenant_code = #{tenantCode}
